package com.library.springbootlibrary.config;

import org.springframework.context.annotation.Configuration;
import org.springframework.data.rest.core.config.RepositoryRestConfiguration;
import org.springframework.data.rest.webmvc.config.RepositoryRestConfigurer;
import org.springframework.http.HttpMethod;
import org.springframework.web.servlet.config.annotation.CorsRegistry;

import com.library.springbootlibrary.entity.Book;

//with this class we're going to block POST, PUT, DELETE http requests which are automatically generated by BookRepositoory interface
@Configuration
public class MyDataRestConfig implements RepositoryRestConfigurer{

	private String theAllowedOrigins = "https://localhost:3000";
	
	@Override
	public void configureRepositoryRestConfiguration(RepositoryRestConfiguration config, CorsRegistry cors) {
		
		HttpMethod[] theUnsupportedActions = {HttpMethod.POST, HttpMethod.PATCH, HttpMethod.DELETE, HttpMethod.PUT};
		
		config.exposeIdsFor(Book.class);
		
		//disable POST, PUT, DELETE http requests
		disabbleHttpMethods(Book.class, config, theUnsupportedActions);
		
		//map to only allow the allowed origin url to access
		/*Configure CORS Mapping*/
		cors.addMapping(config.getBasePath() + "/**").allowedOrigins(theAllowedOrigins).allowedHeaders("*");
	}

	private void disabbleHttpMethods(Class theClass, RepositoryRestConfiguration config, HttpMethod[] theUnsupportedActions) {
		// TODO Auto-generated method stub
		config.getExposureConfiguration().forDomainType(theClass)
				.withItemExposure((metdata, httpMethods) ->
				httpMethods.disable(theUnsupportedActions))
				.withCollectionExposure((metdata, httpMethods) ->
				httpMethods.disable(theUnsupportedActions));
		
	}
	
}
